import React, { useState, useEffect } from 'react';
import { Viewer, SpecialZoomLevel } from '@react-pdf-viewer/core';
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';
import { OnHighlightKeyword } from '@react-pdf-viewer/search';
import { Worker } from '@react-pdf-viewer/core';
import Split from 'react-split';

import '@react-pdf-viewer/core/lib/styles/index.css';
import '@react-pdf-viewer/default-layout/lib/styles/index.css';
import './App.css';

interface Section {
    heading: string;
    summary: string;
    detailed_summary: string;
    section_text: string;
    page_number: number;
    subsections?: Section[];
}

interface ESMADocument {
    Name: string;
    overall_summary: string;
    executive_summary: string;
    sections: Section[];
}

const renderMarkdown = (text: string) => {
    return text.split(/(\*\*[^*]+\*\*)/).map((part, index) => {
        if (part.startsWith('**') && part.endsWith('**')) {
            return <strong key={index}>{part.slice(2, -2)}</strong>;
        }
        return part;
    });
};

interface SectionComponentProps {
    section: Section;
    level: number;
    onPageClick: (pageNumber: number) => void;
}

const SectionComponent: React.FC<SectionComponentProps> = ({ section, level, onPageClick }) => {
    const [isExpanded, setIsExpanded] = useState(false);
    const [showFullText, setShowFullText] = useState(false);

    const handlePageClick = (e: React.MouseEvent) => {
        e.stopPropagation();
        onPageClick(section.page_number);
    };

    return (
        <div style={{ marginLeft: `${level * 20}px` }} className="section">
            <div 
                className="section-box"
                onClick={() => setIsExpanded(!isExpanded)}
            >
                <div className="section-header">
                    <span className="section-title">{section.heading}</span>
                    <div className="section-controls">
                        <button 
                            className="page-button"
                            onClick={handlePageClick}
                            title={`Go to page ${section.page_number}`}
                        >
                            p.{section.page_number}
                        </button>
                        <span className="expand-arrow">{isExpanded ? '▼' : '▶'}</span>
                    </div>
                </div>
                <p className="section-summary">{renderMarkdown(section.summary)}</p>

                {isExpanded && (
                    <div className="section-detailed-summary">
                        <p>{renderMarkdown(section.detailed_summary)}</p>
                        <div 
                            className="full-text-toggle"
                            onClick={(e) => {
                                e.stopPropagation();
                                setShowFullText(!showFullText);
                            }}
                        >
                            {showFullText ? 'Hide Full Text ▼' : 'Show Full Text ▶'}
                        </div>
                        {showFullText && (
                            <div className="full-text">
                                <p>{renderMarkdown(section.section_text)}</p>
                            </div>
                        )}
                    </div>
                )}
            </div>

            {isExpanded && section.subsections?.map((subsection, index) => (
                <SectionComponent 
                    key={index} 
                    section={subsection} 
                    level={level + 1}
                    onPageClick={onPageClick}
                />
            ))}
        </div>
    );
};

const App: React.FC = () => {
    const [documentData, setDocumentData] = useState<ESMADocument | null>(null);
    const [execSummaryExpanded, setExecSummaryExpanded] = useState(false);
    const [pdfViewer, setPdfViewer] = useState<any>(null);

    useEffect(() => {
        fetch('/output-esma.json')
            .then(response => response.json())
            .then(data => setDocumentData(data));
    }, []);

    const defaultLayoutPluginInstance = defaultLayoutPlugin({
        toolbarPlugin: {
            searchPlugin: {
                keyword: ['ESMA'],
                onHighlightKeyword: (props: OnHighlightKeyword) => {
                    props.highlightEle.style.outline = '1px dashed blue';
                    props.highlightEle.style.backgroundColor = 'rgba(0, 0, 255, 0.2)';
                },
            },
        },
    });

    const handlePageNavigation = (pageNumber: number) => {
        if (pdfViewer) {
            // Convert from 1-based page number to 0-based page index
            pdfViewer.jumpToPage(pageNumber - 1);
        }
    };

    return (
        <Split 
            className="split-container"
            sizes={[30, 70]}
            minSize={200}
            gutterSize={10}
            snapOffset={30}
        >
            <div className="left-pane">
                {documentData && (
                    <>
                        <h1>{documentData.Name}</h1>
                        <div className="summary-section">
                            <h2>Overall Summary</h2>
                            <p>{renderMarkdown(documentData.overall_summary)}</p>
                        </div>

                        <div className="summary-section">
                            <h2 onClick={() => setExecSummaryExpanded(!execSummaryExpanded)} 
                                style={{ cursor: 'pointer' }}>
                                Executive Summary {execSummaryExpanded ? '▼' : '▶'}
                            </h2>
                            {execSummaryExpanded && <p>{renderMarkdown(documentData.executive_summary)}</p>}
                        </div>

                        <div className="sections">
                            <h2>Sections</h2>
                            {documentData.sections.map((section, index) => (
                                <SectionComponent 
                                    key={index} 
                                    section={section} 
                                    level={0}
                                    onPageClick={handlePageNavigation}
                                />
                            ))}
                        </div>
                    </>
                )}
            </div>
            <div className="right-pane">
                <Worker workerUrl="https://unpkg.com/pdfjs-dist@3.11.174/build/pdf.worker.min.js">
                    <Viewer
                        fileUrl="/esma_strategy_2023-2028.pdf"
                        plugins={[defaultLayoutPluginInstance]}
                        onDocumentLoad={(e) => setPdfViewer(e.viewer)}
                    />
                </Worker>
            </div>
        </Split>
    );
};

export default App;
